

public class areaofrectangle {
   void calculate() {
	float length=5.6f;
	float breadth=4.7f;
	
	double area= length*breadth;
	System.out.println("area of rectangle ="+area);
   }
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		areaofrectangle a= new areaofrectangle();
		a.calculate();

	}

	
}
